#!/bin/bash
whois "$1" | awk -F': ' '/Registrant|Admin|Tech/ && /Organization|State\/Province|Country|Email/ {gsub(/^[ \t]+/,"",$2); data[$1]=$2} END{tab="\t"; print "Registrant Organization"tab data["Registrant Organization"]; print "Registrant State/Province"tab data["Registrant State/Province"]; print "Registrant Country"tab data["Registrant Country"]; print "Registrant Email"tab data["Registrant Email"]; print "Admin Organization"tab data["Admin Organization"]; print "Admin State/Province"tab data["Admin State/Province"]; print "Admin Country"tab data["Admin Country"]; print "Admin Email"tab data["Admin Email"]; print "Tech Organization"tab data["Tech Organization"]; print "Tech State/Province"tab data["Tech State/Province"]; print "Tech Country"tab data["Tech Country"]; print "Tech Email"tab data["Tech Email"]}' > "$1".csv
